import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles, ThemeProvider } from '@material-ui/core/styles';
import { createMuiTheme } from '@material-ui/core/styles'; 
import FormControl from '@material-ui/core/FormControl';
import Container from '@material-ui/core/Container';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { white } from 'material-ui/styles/colors';


function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Vox-Popuili
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const theme = createMuiTheme({
  palette: {
    primary: {
      light: '',
      main: '#3f50b5',
      dark: '',
      contrastText: '#fff',
    },
    secondary: {
      light: '',
      main: '#f44336',
      dark: '',
      contrastText: '#000',
    },
  },
});



const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function SignUp() {
  const classes = useStyles();
  
  //states for enterred data


  //Dialog
  const [open, setOpen] = React.useState(false);
  const [submitted, setSub] = React.useState(false);

  //Form data
  const [formData, updateForm] = React.useState({
    userName: "",
    firstName: "", 
    lastName: "", 
    email: "", 
    password1: "", 
    password2: ""
  });

  const [formErrs, updateErrs] = React.useState({
    userName: "",
    firstName: "", 
    lastName: "", 
    email: "", 
    password1: "", 
    password2: ""
  });
  //////

  //Not too sure why i needed prev state...oh well
  const handleChange = (evt) => {
    const {name, value} = evt.target;
    updateForm(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleErrors = formErrs => {
    let valid = true;

    Object.values(formErrs).forEach( val => {
      val.length > 0 && (valid = false); //Note this is assignment
    });


    return valid;
  }

  const handleSubmission = (evt) => {
    evt.preventDefault();
    console.log("Testing handleSubmission");
    console.log(formData);

  }

  //Making of open/close states for dialog
  const handleClickOpen = () => {
    setOpen(true);
  }

  const handleClose = () => {
    setOpen(false);
  } 

  return (
    <ThemeProvider theme={theme}>
    <div>
      <Button variant="contained" color="primary" onClick={handleClickOpen}>
        Sign-Up Here!
      </Button>
    <Dialog open={open} onClose={handleClose} noValidate>
      <FormControl>
        <Container component="main" maxWidth="xs">      
            <CssBaseline />
              <div className={classes.paper}>
                <Avatar className={classes.avatar}>
                  <LockOutlinedIcon />
                </Avatar>
                <Typography component="h1" variant="h5">
                  Sign up to speak out
                </Typography>
                <form className={classes.form} noValidate>
                  <Grid container spacing={2}>
                  <Grid item xs={12}>
                      <TextField
                        name="userName"
                        error
                        variant="outlined"
                        required
                        fullWidth
                        id="userName"
                        label="User Name"
                        autoFocus
                        onChange = {handleChange}
                      />
                    </Grid>
                    <Grid item xs={6}>
                      <TextField
                        autoComplete="fname"
                        name="firstName"
                        variant="outlined"
                        fullWidth
                        id="firstName"
                        label="First Name"
                        onChange = {handleChange}
                      />
                    </Grid>
                    <Grid item xs={6}>
                      <TextField
                        variant="outlined"
                        fullWidth
                        id="lastName"
                        label="Last Name"
                        name="lastName"
                        autoComplete="lname"
                        onChange = {handleChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        required
                        fullWidth
                        id="email"
                        label="Email Address"
                        name="email"
                        autoComplete="email"
                        onChange = {handleChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        required
                        fullWidth
                        name="password1"
                        label="Password"
                        type="password"
                        id="password1"
                        onChange = {handleChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        variant="outlined"
                        required
                        fullWidth
                        name="password2"
                        label="Confirm Password"
                        type="password"
                        id="password2"
                        onChange = {handleChange}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <FormControlLabel
                        control={<Checkbox value="allowExtraEmails" color="primary" />}
                        label="I want to receive inspiration and updates via email."
                      />
                    </Grid>
                  </Grid>
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    color="primary"
                    className={classes.submit}
                    disabled={submitted}
                    onClick={handleSubmission}
                  >
                    Sign Up
                  </Button>
                  <Grid container justify="flex-end">
                    <Grid item>
                      <Link href="/" variant="body2">
                        Already have an account? Login
                      </Link>
                    </Grid>
                  </Grid>
                </form>
              </div>
              <Box mt={5}>
                <Copyright />
              </Box>
            </Container>
        </FormControl>
    </Dialog>
  </div>
  </ThemeProvider>
  );
}
